                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
//a class which contains only one super class and multiple sub classes and sub class directly extends super class is called hierarchical inheritance.
class HierarchicalInheritance{
    public static void main(String[] args) {
        Bike b=new Bike();
        b.showPrice();
        b.finalPrice();
      FourWheeler f=new FourWheeler();
      f.showPrice(); 
      f.finalPrice();
 }
}
class Vehicle{
   public double basePrice=100000;
    public void showPrice(){
        System.out.println("the price of vehicle is:"+basePrice);
    }
}
class Bike extends Vehicle{
    double increasePrice=0.20;
    void finalPrice(){
        basePrice+=basePrice*increasePrice;
        System.out.println("After modification the price of bike is:"+basePrice);
    }
}
class FourWheeler extends Vehicle{
    double increasePrice=0.40;
    void finalPrice(){
        basePrice+=basePrice*increasePrice;
        System.out.println("After modification the price of car is:"+basePrice);
    }
}